/**
 * Nosto exception message class for errors within the sdk.
 */
class NostoExceptionMessage
{
    /**
     * @var string
     */
    private $key;

    /**
     * @var string
     */
    private $message;

    /**
     * @var string
     */
    private $code;

    /**
     * NostoExceptionMessage constructor.
     * @param string $key
     * @param string $message
     * @param string $publicMessage
     * @param string $code
     */
    public function __construct($key, $message, $code = "")
    {
        $this->key = $key;
        $this->message = $message;
        $this->code = $code;
    }

    /**
     * @return string
     */
    public function getCode()
    {
        return $this->code;
    }

    /**
     * @param string $code
     */
    public function setCode($code)
    {
        $this->code = $code;
    }

    /**
     * @return string
     */
    public function getKey()
    {
        return $this->key;
    }

    /**
     * @param string $key
     */
    public function setKey($key)
    {
        $this->key = $key;
    }

    /**
     * @return string
     */
    public function getMessage()
    {
        return $this->message;
    }

    /**
     * @param string $message
     */
    public function setMessage($message)
    {
        $this->message = $message;
    }
}
